import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import axios from 'axios';
import './CapsuleApp.css';

const API_BASE = 'http://localhost:5000/api';

function CapsuleApp() {
  const { user, logout } = useAuth();
  const [capsules, setCapsules] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  const [formData, setFormData] = useState({
    title: '',
    message: '',
    delivery_date: '',
    recipient_type: 'self',
    recipient_email: '',
    recipients: [],
    category: 'personal',
    tags: [],
    media_files: []
  });

  useEffect(() => {
    fetchCapsules();
  }, []);

  const fetchCapsules = async (query = '', category = '') => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (query) params.append('q', query);
      if (category) params.append('category', category);
      
      const response = await axios.get(`${API_BASE}/capsules?${params}`);
      setCapsules(response.data);
    } catch (error) {
      console.error('Error fetching capsules:', error);
      if (error.response?.status === 401) {
        logout();
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    fetchCapsules(query, selectedCategory);
  };

  const handleCategoryFilter = (category) => {
    setSelectedCategory(category);
    fetchCapsules(searchQuery, category);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.title || !formData.message || !formData.delivery_date) {
      alert('Please fill in all required fields');
      return;
    }

    try {
      setLoading(true);
      await axios.post(`${API_BASE}/capsules`, formData);
      
      // Reset form
      setFormData({
        title: '',
        message: '',
        delivery_date: '',
        recipient_type: 'self',
        recipient_email: '',
        recipients: [],
        category: 'personal',
        tags: [],
        media_files: []
      });
      setShowForm(false);
      await fetchCapsules();
      alert('Time capsule created successfully!');
    } catch (error) {
      console.error('Error creating capsule:', error);
      alert('Error creating capsule. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const deleteCapsule = async (id) => {
    if (window.confirm('Are you sure you want to delete this capsule?')) {
      try {
        await axios.delete(`${API_BASE}/capsules/${id}`);
        await fetchCapsules();
      } catch (error) {
        console.error('Error deleting capsule:', error);
      }
    }
  };

  const calculateCountdown = (deliveryDate) => {
    const today = new Date();
    const delivery = new Date(deliveryDate);
    const diffTime = delivery - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays > 0) {
      return `${diffDays} day${diffDays !== 1 ? 's' : ''}`;
    } else if (diffDays === 0) {
      return 'Today!';
    } else {
      return 'Ready to open!';
    }
  };

  return (
    <div className="capsule-app">
      <header className="app-header">
        <div className="container">
          <div className="header-content">
            <div>
              <h1><i className="fas fa-time-capsule"></i> Time Capsule</h1>
              <p>Welcome back, {user.name}!</p>
            </div>
            <button onClick={logout} className="btn btn-secondary">
              Logout
            </button>
          </div>
        </div>
      </header>

      <div className="container">
        {/* Search and Filter Section */}
        <div className="search-section">
          <div className="search-box">
            <i className="fas fa-search"></i>
            <input
              type="text"
              placeholder="Search capsules..."
              value={searchQuery}
              onChange={handleSearch}
            />
          </div>
          <div className="category-filters">
            <button 
              className={selectedCategory === '' ? 'btn active' : 'btn'}
              onClick={() => handleCategoryFilter('')}
            >
              All
            </button>
            <button 
              className={selectedCategory === 'personal' ? 'btn active' : 'btn'}
              onClick={() => handleCategoryFilter('personal')}
            >
              Personal
            </button>
            <button 
              className={selectedCategory === 'birthday' ? 'btn active' : 'btn'}
              onClick={() => handleCategoryFilter('birthday')}
            >
              Birthday
            </button>
            <button 
              className={selectedCategory === 'travel' ? 'btn active' : 'btn'}
              onClick={() => handleCategoryFilter('travel')}
            >
              Travel
            </button>
          </div>
        </div>

        <div className="app-controls">
          <button 
            className="btn btn-primary"
            onClick={() => setShowForm(!showForm)}
            disabled={loading}
          >
            <i className="fas fa-plus"></i> {showForm ? 'Cancel' : 'Create New Capsule'}
          </button>
        </div>

        {/* Rest of your capsule form and list components */}
        {/* ... (include your existing form and capsule list code here) ... */}
        
      </div>
    </div>
  );
}

export default CapsuleApp;
