import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext.js';
import axios from 'axios';

const API_BASE = 'http://localhost:5000/api';

function CapsuleApp() {
  const { user, logout } = useAuth();
  const [capsules, setCapsules] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [viewingCapsule, setViewingCapsule] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    message: '',
    delivery_date: '',
    recipient_type: 'self',
    recipient_email: '',
    category: 'personal',
    tags: [],
    media_files: []
  });

  useEffect(() => {
    fetchCapsules();
  }, []);

  const fetchCapsules = async (query = '', category = '') => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (query) params.append('q', query);
      if (category) params.append('category', category);
      
      const response = await axios.get(`${API_BASE}/capsules?${params}`);
      setCapsules(response.data);
    } catch (error) {
      console.error('Error fetching capsules:', error);
      if (error.response?.status === 401) {
        logout();
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    fetchCapsules(query, selectedCategory);
  };

  const handleCategoryFilter = (category) => {
    setSelectedCategory(category);
    fetchCapsules(searchQuery, category);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    const fileReaders = files.map(file => {
      return new Promise((resolve) => {
        const reader = new FileReader();
        reader.onload = (e) => {
          resolve({
            filename: file.name,
            content: e.target.result,
            type: file.type
          });
        };
        reader.readAsDataURL(file);
      });
    });

    Promise.all(fileReaders).then(fileData => {
      setFormData(prev => ({
        ...prev,
        media_files: [...prev.media_files, ...fileData]
      }));
    });
  };

  const removeFile = (index) => {
    setFormData(prev => ({
      ...prev,
      media_files: prev.media_files.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.title || !formData.message || !formData.delivery_date) {
      alert('Please fill in all required fields');
      return;
    }

    try {
      setLoading(true);
      await axios.post(`${API_BASE}/capsules`, formData);
      
      // Reset form
      setFormData({
        title: '',
        message: '',
        delivery_date: '',
        recipient_type: 'self',
        recipient_email: '',
        category: 'personal',
        tags: [],
        media_files: []
      });
      setShowForm(false);
      await fetchCapsules();
      alert('Time capsule created successfully!');
    } catch (error) {
      console.error('Error creating capsule:', error);
      alert('Error creating capsule. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const deleteCapsule = async (id) => {
    if (window.confirm('Are you sure you want to delete this capsule?')) {
      try {
        await axios.delete(`${API_BASE}/capsules/${id}`);
        await fetchCapsules();
      } catch (error) {
        console.error('Error deleting capsule:', error);
      }
    }
  };

  const viewCapsule = async (capsuleId) => {
    try {
      const response = await axios.get(`${API_BASE}/capsules/${capsuleId}`);
      setViewingCapsule(response.data);
    } catch (error) {
      console.error('Error fetching capsule:', error);
      alert('Error loading capsule details.');
    }
  };

  const closeCapsuleView = () => {
    setViewingCapsule(null);
  };

  const calculateCountdown = (deliveryDate) => {
    const today = new Date();
    const delivery = new Date(deliveryDate);
    const diffTime = delivery - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays > 0) {
      return `${diffDays} day${diffDays !== 1 ? 's' : ''}`;
    } else if (diffDays === 0) {
      return 'Today!';
    } else {
      return 'Ready to open!';
    }
  };

  // Modal Component
  const CapsuleViewModal = ({ capsule, onClose }) => {
    if (!capsule) return null;

    return (
      <div className="modal-overlay" onClick={onClose}>
        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
          <div className="modal-header">
            <h2>{capsule.title}</h2>
            <button className="close-button" onClick={onClose}>
              <i className="fas fa-times"></i>
            </button>
          </div>
          <div className="modal-body">
            <div className="capsule-meta-modal">
              <p><strong>Delivery Date:</strong> {new Date(capsule.delivery_date).toLocaleDateString()}</p>
              <p><strong>Created:</strong> {new Date(capsule.created_at).toLocaleDateString()}</p>
              <p><strong>Category:</strong> {capsule.category || 'personal'}</p>
              <p><strong>Status:</strong> 
                <span className={`status-${capsule.status}`}>
                  {capsule.status === 'ready' ? 'Ready to Open! ðŸŽ‰' : 'Locked ðŸ”’'}
                </span>
              </p>
            </div>
            
            <div className="capsule-message-modal">
              <h3>Your Message:</h3>
              <div className="message-content">
                {capsule.message}
              </div>
            </div>

            {capsule.media_files && capsule.media_files.length > 0 && (
              <div className="capsule-media-modal">
                <h3>Attachments ({capsule.media_files.length})</h3>
                <div className="media-grid">
                  {capsule.media_files.map((file, index) => (
                    <div key={index} className="media-item-modal">
                      {file.type.startsWith('image/') ? (
                        <img 
                          src={`http://localhost:5000/api/media/${file.filename}`} 
                          alt={file.original_name}
                          onError={(e) => {
                            e.target.src = 'data:image/svg+xml;base64,';
                          }}
                        />
                      ) : (
                        <div className="video-item">
                          <i className="fas fa-video"></i>
                          <span>Video Attachment</span>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
          <div className="modal-footer">
            <button className="btn btn-primary" onClick={onClose}>
              Close
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="capsule-app">
      <header className="app-header">
        <div className="container">
          <div className="header-content">
            <div>
              <h1><i className="fas fa-time-capsule"></i> Time Capsule</h1>
              <p>Welcome back, {user.name}!</p>
            </div>
            <button onClick={logout} className="btn btn-secondary">
              Logout
            </button>
          </div>
        </div>
      </header>

      <div className="container">
        {/* Search and Filter Section */}
        <div className="search-section">
          <div className="search-box">
            <i className="fas fa-search"></i>
            <input
              type="text"
              placeholder="Search capsules..."
              value={searchQuery}
              onChange={handleSearch}
            />
          </div>
          <div className="category-filters">
            <button 
              className={selectedCategory === '' ? 'btn active' : 'btn'}
              onClick={() => handleCategoryFilter('')}
            >
              All
            </button>
            <button 
              className={selectedCategory === 'personal' ? 'btn active' : 'btn'}
              onClick={() => handleCategoryFilter('personal')}
            >
              Personal
            </button>
            <button 
              className={selectedCategory === 'birthday' ? 'btn active' : 'btn'}
              onClick={() => handleCategoryFilter('birthday')}
            >
              Birthday
            </button>
            <button 
              className={selectedCategory === 'travel' ? 'btn active' : 'btn'}
              onClick={() => handleCategoryFilter('travel')}
            >
              Travel
            </button>
          </div>
        </div>

        <div className="app-controls">
          <button 
            className="btn btn-primary"
            onClick={() => setShowForm(!showForm)}
            disabled={loading}
          >
            <i className="fas fa-plus"></i> {showForm ? 'Cancel' : 'Create New Capsule'}
          </button>
        </div>

        {showForm && (
          <div className="capsule-form-container">
            <div className="card">
              <div className="card-header">
                <i className="fas fa-plus-circle"></i> Create New Time Capsule
              </div>
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  <div className="form-group">
                    <label htmlFor="title">Capsule Title *</label>
                    <input
                      type="text"
                      id="title"
                      name="title"
                      value={formData.title}
                      onChange={handleInputChange}
                      placeholder="e.g., Birthday Wishes for 2025"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="message">Your Message *</label>
                    <textarea
                      id="message"
                      name="message"
                      value={formData.message}
                      onChange={handleInputChange}
                      placeholder="Write your future message here..."
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Add Photos or Videos (Optional)</label>
                    <div className="upload-area">
                      <input
                        type="file"
                        multiple
                        accept="image/*,video/*"
                        onChange={handleFileUpload}
                      />
                      <i className="fas fa-cloud-upload-alt"></i>
                      <p>Click to upload or drag and drop</p>
                      <p className="small">PNG, JPG, MP4 up to 10MB</p>
                    </div>
                    <div className="media-preview">
                      {formData.media_files.map((file, index) => (
                        <div key={index} className="media-item">
                          {file.type.startsWith('image/') ? (
                            <img src={file.content} alt="Preview" />
                          ) : (
                            <video controls>
                              <source src={file.content} type={file.type} />
                            </video>
                          )}
                          <button
                            type="button"
                            className="remove"
                            onClick={() => removeFile(index)}
                          >
                            <i className="fas fa-times"></i>
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>

                  <div className="form-group">
                    <label htmlFor="category">Category</label>
                    <select
                      id="category"
                      name="category"
                      value={formData.category}
                      onChange={handleInputChange}
                    >
                      <option value="personal">Personal</option>
                      <option value="birthday">Birthday</option>
                      <option value="anniversary">Anniversary</option>
                      <option value="graduation">Graduation</option>
                      <option value="travel">Travel</option>
                      <option value="goals">Goals</option>
                      <option value="other">Other</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label htmlFor="delivery_date">Delivery Date *</label>
                    <input
                      type="date"
                      id="delivery_date"
                      name="delivery_date"
                      value={formData.delivery_date}
                      onChange={handleInputChange}
                      min={new Date().toISOString().split('T')[0]}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="recipient_type">Send To *</label>
                    <select
                      id="recipient_type"
                      name="recipient_type"
                      value={formData.recipient_type}
                      onChange={handleInputChange}
                    >
                      <option value="self">Myself</option>
                      <option value="other">Someone Else</option>
                    </select>
                  </div>

                  {formData.recipient_type === 'other' && (
                    <div className="form-group">
                      <label htmlFor="recipient_email">Recipient's Email *</label>
                      <input
                        type="email"
                        id="recipient_email"
                        name="recipient_email"
                        value={formData.recipient_email}
                        onChange={handleInputChange}
                        placeholder="Enter email address"
                        required
                      />
                    </div>
                  )}

                  <button 
                    type="submit" 
                    className="btn btn-primary btn-block" 
                    disabled={loading}
                  >
                    <i className="fas fa-lock"></i> {loading ? 'Creating...' : 'Lock Time Capsule'}
                  </button>
                </form>
              </div>
            </div>
          </div>
        )}

        <div className="capsules-list">
          <h2>
            <i className="fas fa-archive"></i> Your Time Capsules
            {capsules.length > 0 && <span className="count-badge">{capsules.length}</span>}
          </h2>
          
          {loading ? (
            <div className="loading-state">
              <i className="fas fa-spinner fa-spin"></i>
              <p>Loading capsules...</p>
            </div>
          ) : capsules.length === 0 ? (
            <div className="empty-state">
              <i className="fas fa-inbox"></i>
              <p>No time capsules yet. Create your first one!</p>
            </div>
          ) : (
            capsules.map(capsule => (
              <div 
                key={capsule.id} 
                className="capsule-item clickable-capsule"
                onClick={() => viewCapsule(capsule.id)}
              >
                <div className="capsule-info">
                  <h3>{capsule.title}</h3>
                  <p className="capsule-message">{capsule.message}</p>
                  
                  {capsule.media_files && capsule.media_files.length > 0 && (
                    <div className="capsule-media">
                      <p><i className="fas fa-paperclip"></i> {capsule.media_files.length} attached file(s)</p>
                    </div>
                  )}
                  
                  <div className="capsule-meta">
                    <span>
                      <i className="fas fa-user"></i> 
                      To: {capsule.recipient_type === 'self' ? 'Myself' : capsule.recipient_email}
                    </span>
                    <span>
                      <i className="fas fa-calendar"></i> 
                      Delivery: {new Date(capsule.delivery_date).toLocaleDateString()}
                    </span>
                    <span className="countdown">
                      <i className="fas fa-clock"></i> 
                      {calculateCountdown(capsule.delivery_date)}
                    </span>
                    <span>
                      <i className="fas fa-tag"></i>
                      Category: {capsule.category || 'personal'}
                    </span>
                  </div>
                </div>
                <div className="capsule-actions">
                  <button 
                    className="btn-danger"
                    onClick={(e) => {
                      e.stopPropagation(); // Prevent triggering the capsule view
                      deleteCapsule(capsule.id);
                    }}
                    title="Delete capsule"
                  >
                    <i className="fas fa-trash"></i>
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Capsule Viewing Modal */}
      {viewingCapsule && (
        <CapsuleViewModal 
          capsule={viewingCapsule} 
          onClose={closeCapsuleView} 
        />
      )}
    </div>
  );
}

export default CapsuleApp;
