import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE = 'http://localhost:5000/api';

function App() {
  const [capsules, setCapsules] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    message: '',
    delivery_date: '',
    recipient_type: 'self',
    recipient_email: '',
    media_files: []
  });

  useEffect(() => {
    fetchCapsules();
  }, []);

  const fetchCapsules = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE}/capsules`);
      setCapsules(response.data);
    } catch (error) {
      console.error('Error fetching capsules:', error);
      alert('Error loading capsules. Make sure the backend server is running on port 5000.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    const fileReaders = files.map(file => {
      return new Promise((resolve) => {
        const reader = new FileReader();
        reader.onload = (e) => {
          resolve({
            filename: file.name,
            content: e.target.result,
            type: file.type
          });
        };
        reader.readAsDataURL(file);
      });
    });

    Promise.all(fileReaders).then(fileData => {
      setFormData(prev => ({
        ...prev,
        media_files: [...prev.media_files, ...fileData]
      }));
    });
  };

  const removeFile = (index) => {
    setFormData(prev => ({
      ...prev,
      media_files: prev.media_files.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate form
    if (!formData.title || !formData.message || !formData.delivery_date) {
      alert('Please fill in all required fields: Title, Message, and Delivery Date');
      return;
    }

    if (formData.recipient_type === 'other' && !formData.recipient_email) {
      alert('Please enter recipient email');
      return;
    }

    try {
      setLoading(true);
      await axios.post(`${API_BASE}/capsules`, formData);
      
      // Reset form
      setFormData({
        title: '',
        message: '',
        delivery_date: '',
        recipient_type: 'self',
        recipient_email: '',
        media_files: []
      });
      setShowForm(false);
      await fetchCapsules();
      alert('Time capsule created successfully!');
    } catch (error) {
      console.error('Error creating capsule:', error);
      alert('Error creating capsule. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const deleteCapsule = async (id) => {
    if (window.confirm('Are you sure you want to delete this capsule?')) {
      try {
        await axios.delete(`${API_BASE}/capsules/${id}`);
        await fetchCapsules();
      } catch (error) {
        console.error('Error deleting capsule:', error);
        alert('Error deleting capsule.');
      }
    }
  };

  const calculateCountdown = (deliveryDate) => {
    const today = new Date();
    const delivery = new Date(deliveryDate);
    const diffTime = delivery - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays > 0) {
      return `${diffDays} day${diffDays !== 1 ? 's' : ''}`;
    } else if (diffDays === 0) {
      return 'Today!';
    } else {
      return 'Ready to open!';
    }
  };

  return (
    <div className="App">
      <header className="app-header">
        <div className="container">
          <h1><i className="fas fa-time-capsule"></i> Time Capsule</h1>
          <p>Send messages to your future self or loved ones</p>
        </div>
      </header>

      <div className="container">
        <div className="app-controls">
          <button 
            className="btn btn-primary"
            onClick={() => setShowForm(!showForm)}
            disabled={loading}
          >
            {showForm ? 'Cancel' : 'Create New Capsule'}
          </button>
        </div>

        {showForm && (
          <div className="capsule-form-container">
            <div className="card">
              <div className="card-header">
                <i className="fas fa-plus-circle"></i> Create New Time Capsule
              </div>
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  <div className="form-group">
                    <label htmlFor="title">Capsule Title *</label>
                    <input
                      type="text"
                      id="title"
                      name="title"
                      value={formData.title}
                      onChange={handleInputChange}
                      placeholder="e.g., Birthday Wishes for 2025"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="message">Your Message *</label>
                    <textarea
                      id="message"
                      name="message"
                      value={formData.message}
                      onChange={handleInputChange}
                      placeholder="Write your future message here..."
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Add Photos or Videos</label>
                    <div className="upload-area">
                      <input
                        type="file"
                        multiple
                        accept="image/*,video/*"
                        onChange={handleFileUpload}
                      />
                      <i className="fas fa-cloud-upload-alt"></i>
                      <p>Click to upload or drag and drop</p>
                      <p className="small">PNG, JPG, MP4 up to 10MB</p>
                    </div>
                    <div className="media-preview">
                      {formData.media_files.map((file, index) => (
                        <div key={index} className="media-item">
                          {file.type.startsWith('image/') ? (
                            <img src={file.content} alt="Preview" />
                          ) : (
                            <video controls>
                              <source src={file.content} type={file.type} />
                            </video>
                          )}
                          <button
                            type="button"
                            className="remove"
                            onClick={() => removeFile(index)}
                          >
                            <i className="fas fa-times"></i>
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>

                  <div className="form-group">
                    <label htmlFor="delivery_date">Delivery Date *</label>
                    <input
                      type="date"
                      id="delivery_date"
                      name="delivery_date"
                      value={formData.delivery_date}
                      onChange={handleInputChange}
                      min={new Date().toISOString().split('T')[0]}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="recipient_type">Send To *</label>
                    <select
                      id="recipient_type"
                      name="recipient_type"
                      value={formData.recipient_type}
                      onChange={handleInputChange}
                    >
                      <option value="self">Myself</option>
                      <option value="other">Someone Else</option>
                    </select>
                  </div>

                  {formData.recipient_type === 'other' && (
                    <div className="form-group">
                      <label htmlFor="recipient_email">Recipient's Email *</label>
                      <input
                        type="email"
                        id="recipient_email"
                        name="recipient_email"
                        value={formData.recipient_email}
                        onChange={handleInputChange}
                        placeholder="Enter email address"
                        required
                      />
                    </div>
                  )}

                  <button type="submit" className="btn btn-primary btn-block" disabled={loading}>
                    <i className="fas fa-lock"></i> {loading ? 'Creating...' : 'Lock Time Capsule'}
                  </button>
                </form>
              </div>
            </div>
          </div>
        )}

        <div className="capsules-list">
          <h2>
            <i className="fas fa-archive"></i> Your Time Capsules
            {capsules.length > 0 && <span className="count-badge">{capsules.length}</span>}
          </h2>
          
          {loading ? (
            <div className="loading-state">
              <i className="fas fa-spinner fa-spin"></i>
              <p>Loading capsules...</p>
            </div>
          ) : capsules.length === 0 ? (
            <div className="empty-state">
              <i className="fas fa-inbox"></i>
              <p>No time capsules yet. Create your first one!</p>
            </div>
          ) : (
            capsules.map(capsule => (
              <div key={capsule.id} className="capsule-item">
                <div className="capsule-info">
                  <h3>{capsule.title}</h3>
                  <p>{capsule.message}</p>
                  <div className="capsule-meta">
                    <span>
                      <i className="fas fa-user"></i> 
                      To: {capsule.recipient_type === 'self' ? 'Myself' : capsule.recipient_email}
                    </span>
                    <span>
                      <i className="fas fa-calendar"></i> 
                      Delivery: {new Date(capsule.delivery_date).toLocaleDateString()}
                    </span>
                    <span className="countdown">
                      <i className="fas fa-clock"></i> 
                      {calculateCountdown(capsule.delivery_date)}
                    </span>
                  </div>
                </div>
                <div className="capsule-actions">
                  <button 
                    className="btn-danger"
                    onClick={() => deleteCapsule(capsule.id)}
                    title="Delete capsule"
                  >
                    <i className="fas fa-trash"></i>
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      <footer className="app-footer">
        <div className="container">
          <p>Time Capsule App &copy; 2023 | Send messages to your future self and loved ones</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
